pair A,B,C,D,E,F,G;

A=(-20,0);
B=(120,0);
C=(0,-20);
D=(0,120);
E=(-6.2,-8);
F=(60,90);
G=(70,105);

dot(F);

draw(A--B,Arrows);
draw(C--D,Arrows);
draw(E--G,Arrows);

label("(20,30)",G,SE);
label("$y=1.5x$",G,N);
[/asy]

