import three;
size(180);
currentprojection = perspective(6,3,2);
triple A, B, C, D, M;
A = (0,0,1);
B = (1,0,0);
C = (0.5,sqrt(3)/2,0);
D = (0.5,sqrt(3)/6,sqrt(6)/3);
M = (C + D)/2;
draw(B--C--D--B);
draw(A--B);
draw(A--C);
draw(A--D);
